import React from "react";
import TodoList, { ITodoItem } from "../components/TodoList";
import Link from "next/link";
import "./index.scss";
import { inject, observer } from "mobx-react";
import { Store } from "../store";

interface Props {
	store: Store;
}

@inject("store")
@observer
class Index extends React.Component<Props> {
	state = {
		inputValue: "",
	};
	// 생성자
	constructor(props) {
		super(props);
		this.handleAppendTodo = this.handleAppendTodo.bind(this);
	}
	// 랜더링
	render() {
		return (
			<div id="app">
				<div className="todo">
					<input type="text" className="todo__input" onKeyPress={this.handleAppendTodo} value={this.state.inputValue} />
					<TodoList list={this.props.store.todolist}></TodoList>
				</div>
			</div>
		);
	}

	// 엔터 쳤을 시 Todo 추가
	handleAppendTodo(e: React.KeyboardEvent) {
		if (e.key == "Enter") {
			// TODO: value 비워야함
			const { value } = e.target as HTMLInputElement;
			this.props.store.addTodo({
				name: value,
				isFinished: false,
			});
			this.setState({
				inputValue: "",
			});
		}
	}
}

export default Index;
